import { Given , And , Then , When} from "cypress-cucumber-preprocessor/steps";

import LoginPage from 'cypress/integration/PageObjects/loginPage';
const loginPage = new LoginPage();



Given('User is on saucedemo login page', ()=> {
    cy.visit(Cypress.env('flipkart_url'));
    loginPage.clickOnLoginBtnn();
})


When('User enter username as {}', (Username) => {
  loginPage.getUserName(Username);
})

When('User enter password as {}', (Password) => {
    loginPage.getPassword(Password);
})

Then('User clicked on login button', () => {
    loginPage.clickOnLoginButton();
  //  loginPage.clickOnSearchBox();
    
})

When('User Enter Product Name as {}', (ProductName) => {
   loginPage.clickOnMobilesBtnn();
   loginPage.clickOnSelectData();


   loginPage.productSelection();
   
})






    

    
